sed -i 's/export const mergePullRequest =/export const checkPullRequestMerged = async (owner: string, repo: string, pullNumber: number) => {\n    const pat = getGitHubPat();\n    if (!pat) return false;\n    try {\n        const response = await fetch(`https:\/\/api.github.com\/repos\/${owner}\/${repo}\/pulls\/${pullNumber}`, {\n            headers: {\n                "Authorization": `token ${pat}`,\n                "Accept": "application\/vnd.github.v3+json",\n            }\n        });\n        if (response.ok) {\n            const data = await response.json();\n            return data.merged === true;\n        }\n    } catch (e) {}\n    return false;\n};\n\nexport const mergePullRequest =/' src/api/client.ts
